mapper.select:
  	SELECT 
  					COUNT(*) 
  	FROM 
  					TST_USER


  		WHERE 
  						TST_USER_ID = #{tst_user_id}


  		WHERE 
  						TST_USER_NICKNAME = #{tst_user_nickname}


			WHERE 
  						TST_USER_EMAIL = #{tst_user_email}

mapper.update:
UPDATE 
TST_USER 
					set TST_USER_KEY ='Y'
WHERE 
					TST_USER_ID = #{tst_user_id}
AND 
					TST_USER_KEY = #{tst_user_key}
AND 
					TST_USER_EMAIL = #{tst_user_email}
